import { useHistory } from "react-router";
import "./PurchaseProof.css"
import { useState, useEffect } from "react"
import Axios from "axios"
import {useLayoutEffect} from  "react"
import TextField from "@material-ui/core/TextField";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListSubheader from '@mui/material/ListSubheader';
import Select from '@mui/material/Select';
import OutlinedInput from '@mui/material/OutlinedInput';
import Register from "./Register";
import React, { Component } from 'react';
import { withRouter } from "react-router-dom";








class PurchaseProof extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            user: null,
            options: [
                {value: 'VISA', label: 'VISA'},
                {value: 'MasterCard', label: 'MasterCard'}
            ]
        }

        this.handleSelectedCard.bind(this)
    }


    componentDidMount() {
        this.setState({user: localStorage.getItem('user')}, () => {console.log(this.state.user)})
    }


    handleSelectedCard (e) {
        console.log(e)
    }

    render() {

        return (
            <div className="CardSelect">
                <label for="Card" style={{marginLeft: "900px", marginTop:"100px", fontFamily:'revert', fontSize: '25px', textAlign:'center'}}>Select Card:</label>
                <select class="select-css" name="Card" onChange={this.handleSelectedCard}> 
                    <option>MasterCard</option> 
                    <option>Visa</option> 
                </select>
                <p><input type="text" class="mask-card-number form-control" placeholder="Номер карты"></input></p>
                <p><input type="text" class="mask-card-date form-control" placeholder="Срок действия"></input></p>
                <p><input type="text" class="mask-card-code form-control" placeholder="CVV2/CVC2"></input></p>
                <link rel="stylesheet" href="https://snipp.ru/cdn/bootstrap/3.4.1/bootstrap.min.css"></link>

                <script src="https://snipp.ru/cdn/jquery/2.1.1/jquery.min.js"></script>
                <script src="https://snipp.ru/cdn/maskedinput/jquery.maskedinput.min.js"></script>
                <script>
                $('.mask-card-number').mask('9999 9999 9999 9999');
                $('.mask-card-date').mask('99/99');
                $('.mask-card-code').mask('999');
                </script>
            </div>
        )
    }

}

export default withRouter(PurchaseProof)
