import { useHistory } from "react-router";
import "./Search.css"
import { useState, useEffect } from "react"
import Axios from "axios"
import Select from "react-select"
import {useLayoutEffect} from  "react"




let options = []

function Search() {
    let [selectedOption, setSelectedOption] = useState(null);
    let [cars, setCars] = useState([null])
    let [colors, setColors] = useState(null)


    const styles = {
  container: base => ({
    ...base,
    flex: 1
  })
};

    useEffect(() => {
        Axios.get('http://localhost:3001/auto', {
        }).then((response) => {
            if (response.data) {
                setCars(response.data.cars)
                setColors(response.data.colors)
                console.log(response.data)
                for (let i = 0; i < cars[0].length; i++) {
                    options.push({label: cars[0][i].name, value: cars[0][i].name})
                }
            }
        })
}, [])


    return (
        <div className="App" style={{width: '1200px', display: 'flex',  justifyContent:'center', alignItems:'flex-start', height: '100vh'}}>
            <div className="Search">
                    <label>Car&emsp;</label>
                    <Select options={options} />
            </div>
        </div>
    );
}

  export default Search;
