import { useHistory } from "react-router";
import "./Buy.css"
import { useState, useEffect } from "react"
import Axios from "axios"
import {useLayoutEffect} from  "react"
import TextField from "@material-ui/core/TextField";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListSubheader from '@mui/material/ListSubheader';
import Select from '@mui/material/Select';
import OutlinedInput from '@mui/material/OutlinedInput';
import Register from "./Register";
import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import "./Search.css"



let options = []


class Search extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            response: []
        }

        this.submit = this.submit.bind(this)
    }


    async submit (e) {
        const url = 'http://localhost:3001/search';
        const data = {
          name: document.getElementById('search').value
        }


        try {
          const response = await fetch(url, {
            method: 'POST', // или 'PUT'
            body: JSON.stringify(data), // данные могут быть 'строкой' или {объектом}!
            headers: {
              'Content-Type': 'application/json'
            }
          });
          const json = await response.json();
          this.setState({response: json})
          this.forceUpdate()

        } catch (error) {
          console.error('Ошибка:', error);
        }

    }


    render() {
      console.log(this.response)
        return (
            <div className="App" style={{scrollBehavior: "auto"}}>

                <form class="example">
                  <input type="text" placeholder="Dealership" name="search" id="search"></input>
                  <button onClick={this.submit}><i class="fa fa-search"></i></button>
                </form>
                {   
                    this.state.response.map((dealership) => {

                       return (
                           <div>
                                    <img style={{float:"left", height: "492px", width:"640px"}}src={dealership.image}></img>
                                    <h1><strong>AUTO INFO:</strong> </h1>
                                      <p>Adress: <span className="main-info">{dealership.adress}</span></p>
                                      <p>Phone number: <span className="main-info">{dealership.phone_number}</span></p> 
                                      <p>Name: <span className="main-info">{dealership.name}</span></p>            
                           </div>
                       )
                    })
                }
            </div>
        )
    }

}




export default withRouter(Search)
