import { useHistory } from "react-router";
import "./PurchaseProof.css"
import { useState, useEffect } from "react"
import Axios from "axios"
import {useLayoutEffect} from  "react"
import TextField from "@material-ui/core/TextField";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListSubheader from '@mui/material/ListSubheader';
import Select from '@mui/material/Select';
import OutlinedInput from '@mui/material/OutlinedInput';
import Register from "./Register";
import React, { Component } from 'react';
import { withRouter } from "react-router-dom";








class PurchaseProof extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            user: null,
            options: [
                {value: 'VISA', label: 'VISA'},
                {value: 'MasterCard', label: 'MasterCard'}
            ]
        }

        this.handleSelectedCard.bind(this)
    }


    componentDidMount() {
        this.setState({user: localStorage.getItem('user')}, () => {console.log(this.state.user)})
    }


    handleSelectedCard (e) {
        console.log(e)
    }

    render() {

        return (
            <div>
                <label for="Card" style={{marginLeft: "600px", marginTop:"100px", fontFamily:'initial'}}>Select Card:</label>
                <select class="select-css" name="Card" onChange={this.handleSelectedCard}> 
                    <option>MasterCard</option> 
                    <option>Visa</option> 
                </select>
            </div>
        )
    }

}

export default withRouter(PurchaseProof)
