import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import Axios from 'axios'




function App() {

  const [usernameReg, setUsernameReg] = useState("")
  const [passwordReg, setPasswordReg] = useState("")
  const [roleReg, setRoleReg] = useState("")
  const [fullnameReg, setFullNameReg] = useState("")


  const register = () => {
    Axios.post('https://localhost:3001/register', {
      username: usernameReg,
      password: passwordReg, 
      fullname: fullnameReg,
      role: "Admin"
    }).then((response) => {
      console.log(response)
    })
  }

  const checked = (e) => {
      if (e.target.checked) {
        console.log(e.target.value)
    }
  }

  function onlyOne(checkbox) {
    var checkboxes = document.getElementsByName('check')
    checkboxes.forEach((item) => {
        if (item !== checkbox) item.checked = false
    })
}

  return (
    <div className="App">
      <div className="registration">
        <h1>Registration</h1><br></br>
        <label>Full name</label><br></br>
        <input type="text" onChange={(e) => {setFullNameReg(e.target.value)}}/><br></br>
        <label>Username</label><br></br>
        <input type="text" onChange={(e) => {setUsernameReg(e.target.value)}}/><br></br>
        <label>Password</label><br></br>
        <input type="text" onChange={(e) => {setPasswordReg(e.target.value)}}/><br></br>
        <label>Role</label><br></br>
        <input type="radio" onChange={checked} name="Admin" value="Admin"/><br></br>
        <input type="radio" onChange={checked} name="Client" value="Client"/><br></br>
        <input type="radio" onChange={checked} name="Manager" value="Manager"/><br></br>
        <button onClick={register}>Register</button><br></br>
      </div>
      <div className="login">
        <h1>Login</h1>
        <input type="text" placeholder="Username..." />
        <input type="password" placeholder="Password..." />
        <button>Log in</button>
      </div>
    </div>
  );
}

export default App;
