import Axios from 'axios'
import { useHistory } from 'react-router';
import "./App.css"
import Profile from './Profile';


function Login() {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [rememberMe, setRememberMe] = useState("")
    let history = useHistory()

    const login = () => {
      history.push('/profile', { username: username, password: password })
    }

    const handleChange = () => {

    }

    return (
    <div className="App">
        <div className="login">
          <h1>Login</h1><br></br>
          <input type="text" placeholder="Username..." onChange={(e) => {setUsername(e.target.value)}}/><br></br>
          <br></br>
          <input type="password" placeholder="Password..." onChange={(e) => {setPassword(e.target.value)}} /><br></br>
          <br></br>
          <label>
            <input name="rememberMe" checked={rememberMe} onChange={this.handleChange} type="checkbox"/> Remember me
          </label>
          <button onClick={login}>Log in</button>
        </div>
    </div>
    );
  }

  export default Login;
