  }

  setNumber(e) {
    this.state.number = e.target.value
  }

  setName(e) {
    this.state.name = e.target.value
  }

  setExpiry(e) {
    this.state.expiry = e.target.value
  }

  setCvc(e) {
    this.state.cvc = e.target.value
  }

  setFocus(e) {
    this.state.focus = e.target.name
  }


  mountHandler = ({ onMount, onUnMount }) => {
    useEffect(() => {
      onMount()
      return onUnMount
    },[])
    return null
  }


  render () {


    return (
      <div className="PaymentForm">
        <div className="Description">
          <h1>Please, enter your credit card details to make a payment</h1><br></br>
        </div>
        <div className="Card">
          <Cards
            number={number}
            name={name}
            expiry={expiry}
            cvc={cvc}
            focused={focus}
          />
          <form>
            <input
              type="tel"
              name="number"
              placeholder="Card Number"
              maxLength={16}
              value={number}
              onChange={this.setNumber}
              onFocus={(e) => setFocus(e.target.name)}
              ref={ref}
            />
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={name}
              maxLength={20}
              onChange={(e) => setName(e.target.value)}
              onFocus={(e) => setFocus(e.target.name)}
            />
            <input
              type="text"
              name="expiry"
              placeholder="MM/YY"
              value={expiry}
              maxLength={4}
              onChange={(e) => setExpiry(e.target.value)}
              onFocus={(e) => setFocus(e.target.name)}
            />
            <input
              type="tel"
              name="cvc"
              placeholder="CVC"
              value={cvc}
              maxLength={3}
              onChange={(e) => setCvc(e.target.value)}
              onFocus={(e) => setFocus(e.target.name)}
            />
            <button class="button-84" role="button" onClick={submit}>PAY</button>
          </form>
        </div>
      </div>
    );

  }

}


export default withRouter(PaymentForm)
