import { useState } from 'react';
import Axios from 'axios'
import { useHistory } from 'react-router';
import "./App.css"
import Profile from './Profile';


function Login() {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    let [rememberMe, setRememberMe] = useState(false)
    let history = useHistory()

    const login = () => {
      localStorage.setItem('user', rememberMe? username: '')
      sessionStorage.setItem('remember me', rememberMe)
      sessionStorage.setItem('user', username)
      history.push('/profile', { username: username, password: password })
    }

    const handleChange = (e) => {
          if (e.target.checked) {
            setRememberMe(false)
          }
          else {
            setRememberMe(true)
          }

          console.log(rememberMe)
    }

    return (
    <div className="App">
        <div className="login">
          <h1>Login</h1><br></br>
          <input type="text" placeholder="Username..." onChange={(e) => {setUsername(e.target.value)}}/><br></br>
          <br></br>
          <input type="password" placeholder="Password..." onChange={(e) => {setPassword(e.target.value)}} /><br></br>
          <br></br>
          <label style={{margin:"0px 0px 0px 0px", textAlign: "left"}}>
            <input name="rememberMe" onChange={handleChange} type="checkbox"/> Remember me
          </label>
          <br></br>
          <button onClick={login}>Log in</button>
        </div>
    </div>
    );
  }

  export default Login;
