import { useState, useRef, useEffect } from "react";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";
import "./PaymentFormStyles.css"
import { useLocation, useHistory } from "react-router-dom";
import React from "react";
import { withRouter } from "react-router";





class PaymentForm extends React.Component {

  constructor(props) {
    super(props)
    this.submit = this.submit.bind(this)
  }

  state = {
    cvc: '',
    expiry: '',
    focus: '',
    name: '',
    number: '',
  };

  handleInputFocus = (e) => {
    this.setState({ focus: e.target.name });
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value });
  }


  submit = async () => {
    const url = 'http://localhost:3001/payment';
        const data = this.props.location
        data.clientId 


        try {
          const response = await fetch(url, {
            method: 'POST', // или 'PUT'
            body: JSON.stringify(data), // данные могут быть 'строкой' или {объектом}!
            headers: {
              'Content-Type': 'application/json'
            }
          });
          // const json = await response.json();
          // this.setState({response: json})
          this.forceUpdate()

        } catch (error) {
          console.error('Ошибка:', error);
        }
  }



  render () {

    console.log(this.props.location)
    return (
      <div className="PaymentForm">
        <div className="Description">
          <h1>Please, enter your credit card details to make a payment</h1><br></br>
        </div>
        <div className="Card">
          <Cards
            number={this.state.number}
            name={this.state.name}
            expiry={this.state.expiry}
            cvc={this.state.cvc}
            focused={this.state.focus}
          />
          <form>
            <input
              type="tel"
              name="number"
              placeholder="Card Number"
              maxLength={16}
              value={this.state.number}
              onChange={this.handleInputChange}
              onFocus={this.handleInputFocus}
            />
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={this.state.name}
              maxLength={20}
              onChange={this.handleInputChange}
              onFocus={this.handleInputFocus}
            />
            <input
              type="text"
              name="expiry"
              placeholder="MM/YY"
              value={this.state.expiry}
              maxLength={4}
              onChange={this.handleInputChange}
              onFocus={this.handleInputFocus}
            />
            <input
              type="tel"
              name="cvc"
              placeholder="CVC"
              value={this.state.cvc}
              maxLength={3}
              onChange={this.handleInputChange}
              onFocus={this.handleInputFocus}
            />
            <button class="button-84" role="button" onClick={() => {this.submit(this.props.location)}}>PAY</button>
          </form>
        </div>
      </div>
    );

  }

}


export default withRouter(PaymentForm)
