import { useHistory } from "react-router";
import "./PurchaseProof.css"
import { useState, useEffect } from "react"
import Axios from "axios"
import {useLayoutEffect} from  "react"
import TextField from "@material-ui/core/TextField";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListSubheader from '@mui/material/ListSubheader';
import Select from '@mui/material/Select';
import OutlinedInput from '@mui/material/OutlinedInput';
import Register from "./Register";
import React, { Component } from 'react';
import { withRouter } from "react-router-dom";








class PurchaseProof extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            user: null,
            options: [
                {value: 'VISA', label: 'VISA'},
                {value: 'MasterCard', label: 'MasterCard'}
            ]
        }

        this.handleSelectedCard.bind(this)
    }


    componentDidMount() {
        this.setState({user: localStorage.getItem('user')}, () => {console.log(this.state.user)})

        const script1 = document.createElement("script");
        const script2 = document.createElement("script");
        const script3 = document.createElement("script");
        script1.src = "https://code.jquery.com/jquery-3.3.1.min.js";
        script1.async = true;
        script2.src = "https://cdnjs.cloudflare.com/ajax/libs/jquery.mask/1.14.15/jquery.mask.min.js";
        script2.async = true;
        script3.src = "https://code.jquery.com/jquery-3.3.1.min.js";
        script3.async = true;
        document.body.appendChild(script1);

    }


    handleSelectedCard (e) {
        console.log(e)
    }

    render() {

        return (
            <div className="PurchaseProof">
                <div class="checkout_form">

                        <div class="card_number" id="card-container">
                            <input type="text" class="input" id="card" placeholder="0000 0000 0000 0000"></input>
                            <div id="logo"></div>
                        </div>

                        <div class="card_grp">

                            <div class="expiry_date">
                                <input type="text" class="expiry_input" data-mask="00"  placeholder="00"></input>
                                <input type="text" class="expiry_input" data-mask="00" placeholder="00"></input>
                            </div>


                            <div class="cvc">
                                <input type="text" class="cvc_input" data-mask="000" placeholder="000"></input>

                                <div class="cvc_img">
                                    ?
                                <div class="img">
                                    <img src="https://i.imgur.com/2ameC0C.png" alt=""></img>
                                </div>

                                </div>

                            </div>


                            <div class="btn" onClick={() => {console.log('click')}}>
                                pay
                            </div>

                        </div>
                </div>

                <script src="https://code.jquery.com/jquery-3.3.1.min.js"></script>
                <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery.mask/1.14.15/jquery.mask.min.js"></script>
                <script src="./card/card-validator"></script>
            </div>
        )
    }

}

export default withRouter(PurchaseProof)
