import { useEffect } from "react";
import { useLocation, useHistory } from "react-router-dom";
import Axios from 'axios'
import { useState } from 'react';
import {Nav, Navbar, Container} from 'react-bootstrap'

function Profile(){ 

const [fullname, setFullName] = useState("")
const [role, setRole] = useState("")
const [id, setId] = useState("")
const location = useLocation();
const history = useHistory()


// const buy = () => {
//     Axios.
// }


useEffect(() => {
        Axios.post('http://localhost:3001/profile', {
          username: location.state.username,
          password: location.state.password, 
        }).then((response) => {
            console.log(response)
            setFullName(response.data.fullname)
            switch(response.data.role_id) {
                case 1: 
                    setRole("Admin")
                    break;
                case 2: 
                    setRole("Manager")
                    break;
                case 3:
                    setRole("Client")
                    break;
                default:
                    break;
            }
            setId(response.data.id)
        })



})

if (role === "Client") {
  return <div className="App">
             <nav class="navbar navbar-expand-md navbar-dark bg-dark shadow-sm">
  <div class="container">
    <a href="#" class="navbar-brand">
       Standard spacing
    </a>

    <button type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler"><span class="navbar-toggler-icon"></span></button>

    <div id="navbarSupportedContent" class="collapse navbar-collapse">
      <ul class="navbar-nav ml-auto">
        <li class="nav-item active"><a href="#" class="nav-link">Home <span class="sr-only">(current)</span></a></li>
        <li class="nav-item"><a href="#" class="nav-link">About</a></li>
        <li class="nav-item"><a href="#" class="nav-link">Services</a></li>
        <li class="nav-item"><a href="#" class="nav-link">Contact</a></li>
      </ul>
    </div>
  </div>
</nav>

         </div>
}
else if  (role === "Manager") {
    return <div className="App">
            <div className="profile">
                <div id="wrapper">
                    <header></header>
                    <nav>
                        <ul className="top-menu">
                            <li><a href="/">START PAGE</a></li>
                            <li><a href="/login/">LOG OUT</a></li>
                            <li><a href="/contracts/">CONTRACTS</a></li>
                            <li><a href="/cars/">DEALERSHIP CARS</a></li>
                            <li><a href="/cars/">CONTRACTS</a></li>
                        </ul>
                    </nav>
                    <div id="heading"></div>
                    <aside></aside>
                    <section><h1>MANAGER PAGE</h1></section>
                    <section><h1>NAME:{fullname}</h1></section>
                    <section><h1 class="role">ROLE:{role}</h1></section>
                </div>
	            <footer></footer>
            </div>
         </div>
}
else if (role === "Admin") {
    return <div className="App">
    <div className="profile">
        <div id="wrapper">
            <header></header>
            <nav>
                <ul className="top-menu">
                    <li><a href="/">START PAGE</a></li>
                    <li><a href="/login/">LOG OUT</a></li>
                    <li><a href="/managers/">MANAGERS</a></li>
                    <li><a href="/clients/">CLINETS</a></li>
                    <li><a href="/auto/">AUTO</a></li>
                    <li><a href="/dealership/">AUTO DEALERSHIP</a></li>
                    <li><a href="/cars/">CONTRACTS</a></li>

                </ul>
            </nav>
            <div id="heading"></div>
            <aside></aside>
            <section><h1>ADMIN PAGE</h1></section>
            <section><h1>NAME:{fullname}</h1></section>
            <section><h1 className="role">ROLE:{role}</h1></section>
        </div>
        <footer></footer>
    </div>
 </div>
}
else return null
}

export default Profile
