import { useState, useRef, useEffect } from "react";
import Cards from "react-credit-cards";
import "react-credit-cards/es/styles-compiled.css";
import "./PaymentFormStyles.css"
import { useLocation, useHistory } from "react-router-dom";





export default function PaymentForm() {
  const [number, setNumber] = useState("");
  const [name, setName] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvc, setCvc] = useState("");
  const [focus, setFocus] = useState("");
  const location = useLocation();
  const history = useHistory()


  useEffect(() => {
    ref.current.focus();
  }, []);


  async function submit(e) {
    const url = 'http://localhost:3001/payment';
    const data = location.state.car

    console.log('')

    try {
      const response = await fetch(url, {
        method: 'POST', // или 'PUT'
        body: JSON.stringify(data), // данные могут быть 'строкой' или {объектом}!
        headers: {
          'Content-Type': 'application/json'
        }
      });
      // const json = await response.json();
      // this.setState({response: json})
      // this.forceUpdate()

    } catch (error) {
      console.error('Ошибка:', error);
    }
  }



  const ref = useRef(null);
  return (
    <div className="PaymentForm">
      <div className="Description">
        <h1>Please, enter your credit card details to make a payment</h1><br></br>
      </div>
      <div className="Card">
        <Cards
          number={number}
          name={name}
          expiry={expiry}
          cvc={cvc}
          focused={focus}
        />
        <form>
          <input
            type="tel"
            name="number"
            placeholder="Card Number"
            maxLength={16}
            value={number}
            onChange={(e) => setNumber(e.target.value)}
            onFocus={(e) => setFocus(e.target.name)}
            ref={ref}
          />
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={name}
            maxLength={20}
            onChange={(e) => setName(e.target.value)}
            onFocus={(e) => setFocus(e.target.name)}
          />
          <input
            type="text"
            name="expiry"
            placeholder="MM/YY"
            value={expiry}
            maxLength={4}
            onChange={(e) => setExpiry(e.target.value)}
            onFocus={(e) => setFocus(e.target.name)}
          />
          <input
            type="tel"
            name="cvc"
            placeholder="CVC"
            value={cvc}
            maxLength={3}
            onChange={(e) => setCvc(e.target.value)}
            onFocus={(e) => setFocus(e.target.name)}
          />
          <button class="button-84" role="button" onClick={submit}>PAY</button>
        </form>
      </div>
    </div>
  );
}
