import { useHistory } from "react-router";
import "./PurchaseProof.css"
import { useState, useEffect } from "react"
import Axios from "axios"
import {useLayoutEffect} from  "react"
import TextField from "@material-ui/core/TextField";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListSubheader from '@mui/material/ListSubheader';
import Select from '@mui/material/Select';
import OutlinedInput from '@mui/material/OutlinedInput';
import Register from "./Register";
import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import {Helmet} from "react-helmet";








class PurchaseProof extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            user: null,
            options: [
                {value: 'VISA', label: 'VISA'},
                {value: 'MasterCard', label: 'MasterCard'}
            ],
            success: false
        }

        this.handleSelectedCard.bind(this)
    }


    componentDidMount() {
        this.setState({user: localStorage.getItem('user')}, () => {console.log(this.state.user)})

        const script = document.createElement("script");

        script.src = "card-validator";
        script.async = true;

        document.body.appendChild(script);
    }


    handleSelectedCard (e) {
        console.log(e)
    }

    render() {

        if (!this.state.success) {
            return (
                <div className="PurchaseProof">
                    <Helmet>
                                <script src="https://code.jquery.com/jquery-3.3.1.min.js" type="text/javascript" ></script>
                                <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery.mask/1.14.15/jquery.mask.min.js" type="text/javascript" ></script>
                                <script src="http://localhost:3001/file"  type="text/javascript" ></script>
                    </Helmet>
                    <div class="checkout_form">

                            <div class="card_number" id="card-container">
                                <input type="text" class="input" id="card" placeholder="0000 0000 0000 0000"></input>
                                <div id="logo"></div>
                            </div>

                            <div class="card_grp">

                                <div class="expiry_date">
                                    <input type="text" class="expiry_input" data-mask="00"  placeholder="00"></input>
                                    <input type="text" class="expiry_input" data-mask="00" placeholder="00"></input>
                                </div>


                                <div class="cvc">
                                    <input type="text" class="cvc_input" data-mask="000" placeholder="000"></input>

                                    <div class="cvc_img">
                                        ?
                                    <div class="img">
                                        <img src="https://i.imgur.com/2ameC0C.png" alt=""></img>
                                    </div>

                                    </div>

                                </div>



                            </div>
                            <div class="btn" onClick={() => {this.state.success = true}}>
                                pay
                            </div>
                    </div>
                </div>
            )
        } else {
            return(
                <div class="wave">
                <span style="--c:1;">L</span>
                <span style="--c:2;">o</span>
                <span style="--c:3;">a</span>
                <span style="--c:4;">d</span>
                <span style="--c:5;">i</span>
                <span style="--c:6;">n</span>
                <span style="--c:7;">g</span>
                <span style="--c:8;">.</span>
                <span style="--c:9;">.</span>
                <span style="--c:10;">.</span>
              </div>
            )
        }
    }

}

export default withRouter(PurchaseProof)
